beforeJsCode:
// === CLIENT CODE: botón "Logistic" por fila ===
var pageObj = this.pageObj;

// Obtén las keys del registro clickeado
var keys = this.getKeys ? this.getKeys() : Runner.getSelectedKeys(pageObj.pageid);
if (!keys || !Object.keys(keys).length) {
  swal('Atención','Selecciona un registro.','warning');
  return;
}

// Arma editid1=..., editid2=... (PK compuesta soportada)
var params = [];
var i = 1;
Object.keys(keys).forEach(function(k){
  params.push('editid'+(i++)+'='+encodeURIComponent(keys[k]));
});

// Navega a la página alterna
var url = 'solicitudes_edit.php?page=edit1&' + params.join('&');
window.location.href = url;
