beforeJsCode:
  try{
    // --- DEBUG, para verificar que sí entra aquí ---
    // alert('Click carta LIST');
    console.log('row en LIST:', row);

    function limpio(x){
      return (x==null ? "" : String(x).trim());
    }

    // ========== 1) ID de la solicitud desde la fila ==========
    var id = "";

    // En versiones nuevas row.data trae los campos
    if (row && row.data){
      id = limpio(row.data["idsolicitud"]);
    }

    // Fallback: en otras versiones se usa getFieldValue
    if (!id && row && typeof row.getFieldValue === "function"){
      id = limpio(row.getFieldValue("idsolicitud"));
    }

    if (!id){
      alert("No se pudo determinar el ID de la solicitud.\n" +
            "Verifica que 'idsolicitud' está en el SELECT del LIST.");
      return false;
    }

    // ========== 2) Datos del cliente ==========
    var nombre   = "";
    var paterno  = "";
    var materno  = "";
    var cliente  = "";
    var rfc      = "";
    var dn       = "";

    if (row && row.data){
      nombre  = limpio(row.data["nombre"]  || row.data["Nombre"]);
      paterno = limpio(row.data["paterno"] || row.data["Paterno"]);
      materno = limpio(row.data["materno"] || row.data["Materno"]);
      cliente = limpio(row.data["cliente"] || row.data["nombrecliente"]);
      rfc     = limpio(row.data["rfc"]);
      dn      = limpio(row.data["dn"] || row.data["dnpreferido"]);
    } else if (row && typeof row.getFieldValue === "function"){
      nombre  = limpio(row.getFieldValue("nombre"));
      paterno = limpio(row.getFieldValue("paterno"));
      materno = limpio(row.getFieldValue("materno"));
      cliente = limpio(row.getFieldValue("cliente") || row.getFieldValue("nombre_completo"));
      rfc     = limpio(row.getFieldValue("rfc"));
      dn      = limpio(row.getFieldValue("dn") || row.getFieldValue("dnpreferido"));
    }

    var nombreClienteFull = [nombre, paterno, materno]
      .filter(Boolean)
      .join(" ")
      .replace(/\s+/g," ")
      .trim();
    var nombreCliente = nombreClienteFull || cliente || "[Nombre del Cliente]";

    // ========== 3) Fecha actual ==========
    function formatFecha(){
      var d = new Date();
      var meses = ['Ene','Feb','Mar','Abr','May','Jun','Jul','Ago','Sep','Oct','Nov','Dic'];
      var dd = ('0'+d.getDate()).slice(-2);
      var mm = meses[d.getMonth()];
      var yy = d.getFullYear();
      return dd + '/' + mm + '/' + yy;
    }
    var fechaHoy = formatFecha();

    // ========== 4) Ruta del logo ==========
    function buildAbs(path){
      var p = location.pathname;
      var base = p.substring(0, p.lastIndexOf("/") + 1); // /prm1/
      return base + path.replace(/^\/+/, '');
    }
    var LOGO_FILE = 'images/LOGO NEGRO Y AZUL PEQUE.png';
    var logoUrl   = buildAbs(LOGO_FILE);

    function esc(x){ return (x==null ? "" : String(x)); }
    var safeId = id.replace(/[^\w\-]+/g,"_") || "SIN_ID";

    // ========== 5) HTML de la carta ==========
    function buildLetterHTML(){
      var parts = [];
      parts.push('<!doctype html>');
      parts.push('<html lang="es"><head><meta charset="utf-8">');
      parts.push('<title>Carta de Bienvenida - '+esc(nombreCliente)+'</title>');
      parts.push('<style>');
      parts.push('  @page { size: A4; margin: 20mm 18mm 22mm 18mm; }');
      parts.push('  html,body{ height:100%; }');
      parts.push('  body{ font-family: Arial, Helvetica, sans-serif; font-size: 12pt; color:#111; }');
      parts.push('  .wrap{ max-width: 700px; margin: 0 auto; }');
      parts.push('  .logo{ height: 48px; display:block; margin: 0 0 6px 0; }');
      parts.push('  .empresa{ text-align:center; font-weight:700; font-size:14pt; margin-top:4px; }');
      parts.push('  .linea{ height:0; border:0; border-top:1.5px solid #1f2937; margin:10px 0 6px 0; }');
      parts.push('  .fecha{ text-align:right; font-weight:700; margin:0 0 12px 0; }');
      parts.push('  .titulo{ text-align:center; font-size:16pt; font-weight:700; margin:6px 0; }');
      parts.push('  .linea2{ height:0; border:0; border-top:1.5px solid #1f2937; margin:6px 0 16px 0; }');
      parts.push('  .cuerpo{ text-align:justify; text-justify:inter-word; line-height:1.5; }');
      parts.push('  .cuerpo p{ margin:10pt 0 10pt 0; }');
      parts.push('  .firma{ margin-top: 10pt; }');
      parts.push('  .toolbar{ display:flex; gap:10px; justify-content:flex-end; margin:6px 0 14px; }');
      parts.push('  .btn{ padding:8px 12px; border:0; border-radius:8px; cursor:pointer; font-weight:700; color:#fff; }');
      parts.push('  .btn-print{ background:#111; }');
      parts.push('  .btn-doc{ background:#1565c0; display:none; }');
      parts.push('  @media print{ .toolbar{ display:none; } }');
      parts.push('</style></head><body>');
      parts.push('<div class="wrap">');
      parts.push('  <div class="toolbar">');
      parts.push('    <button class="btn btn-print" onclick="window.print()">Imprimir / PDF</button>');
      parts.push('    <button class="btn btn-doc" id="dlDoc">Descargar Word</button>');
      parts.push('  </div>');
      parts.push('  <img class="logo" src="'+esc(logoUrl)+'" alt="Logo">');
      parts.push('  <div class="empresa">Grupo ROCASA Peninsular, S.A. de C.V.</div>');
      parts.push('  <hr class="linea">');
      parts.push('  <div class="fecha">Fecha: '+esc(fechaHoy)+'</div>');
      parts.push('  <div class="titulo">Carta de Bienvenida</div>');
      parts.push('  <hr class="linea2">');
      parts.push('  <div class="cuerpo">');
      parts.push('    <p></p>');
      parts.push('    <p><b>Estimado(a) '+esc(nombreCliente)+':</b></p>');
      parts.push('    <p>Reciba un cordial saludo de parte de todo el equipo de Grupo Rocasa Peninsular. ');
      parts.push('    Nos complace enormemente que haya decidido <b>renovar su plan de servicio con nosotros</b>.</p>');
      parts.push('    <p>Su confianza es el motor que nos impulsa a seguir mejorando día con día, ofreciéndole soluciones de comunicación confiables, modernas y adaptadas a sus necesidades.</p>');
      parts.push('    <p>Queremos agradecerle sinceramente por seguir formando parte de nuestra comunidad de clientes. Valoramos su lealtad y la oportunidad de continuar brindándole un servicio de calidad, respaldado por la atención y el compromiso que nos caracterizan.</p>');
      parts.push('    <p>Nos ponemos a su entera disposición para atender cualquier consulta o gestión relacionada con su servicio en cualquiera de nuestras sucursales o a través de nuestros canales de atención habituales. Nuestro personal estará siempre dispuesto a apoyarle en todo momento.</p>');
      parts.push('    <p>Agradecemos nuevamente la confianza depositada en nosotros y reiteramos nuestro compromiso de seguir siendo su mejor aliado en comunicación.</p>');
      parts.push('    <p></p>');
      parts.push('    <p class="firma">Con aprecio,</p>');
      parts.push('    <p></p>');
      parts.push('    <p></p>');
      parts.push('    <p><b>Grupo Rocasa Peninsular</b></p>');
      parts.push('    <p></p>');
      parts.push('    <p><b>990 101 4993 / gerente.vnp@rocasamexico.com</b></p>');
      parts.push('    <p></p>');
      parts.push('    <p><b>www.rocasamexico.com</b></p>');
      parts.push('  </div>');
      parts.push('</div>');

      parts.push('<script>(function(){');
      parts.push('  var btn = document.getElementById("dlDoc");');
      parts.push('  if(!btn) return;');
      parts.push('  function getHTMLForDoc(){');
      parts.push('    var clone = document.documentElement.cloneNode(true);');
      parts.push('    var tb = clone.querySelector(".toolbar"); if (tb) tb.parentNode.removeChild(tb);');
      parts.push('    var scripts = clone.querySelectorAll("script");');
      parts.push('    for (var i=0;i<scripts.length;i++){ var s = scripts[i]; if (s.parentNode) s.parentNode.removeChild(s); }');
      parts.push('    return "\\ufeff" + "<!doctype html>" + clone.outerHTML;');
      parts.push('  }');
      parts.push('  function saveAsWord(html, filename){');
      parts.push('    if (window.navigator && window.navigator.msSaveOrOpenBlob){');
      parts.push('      var blobIE = new Blob([html], { type: "application/msword;charset=utf-8" });');
      parts.push('      return window.navigator.msSaveOrOpenBlob(blobIE, filename);');
      parts.push('    }');
      parts.push('    try{');
      parts.push('      var blob = new Blob([html], { type: "application/msword;charset=utf-8" });');
      parts.push('      var url  = URL.createObjectURL(blob);');
      parts.push('      var a = document.createElement("a");');
      parts.push('      a.href = url; a.download = filename;');
      parts.push('      document.body.appendChild(a); a.click();');
      parts.push('      setTimeout(function(){ URL.revokeObjectURL(url); if(a.parentNode) a.parentNode.removeChild(a); }, 500);');
      parts.push('      return true;');
      parts.push('    }catch(e){ }');
      parts.push('    try{');
      parts.push('      var dataUrl = "data:application/msword;charset=utf-8," + encodeURIComponent(html);');
      parts.push('      var w = window.open(dataUrl, "_blank");');
      parts.push('      if (!w || w.closed || typeof w.closed === "undefined") { location.href = dataUrl; }');
      parts.push('      return true;');
      parts.push('    }catch(e2){ alert("No fue posible generar el Word: " + e2.message); return false; }');
      parts.push('  }');
      parts.push('  btn.addEventListener("click", function(){');
      parts.push('    var html = getHTMLForDoc();');
      parts.push('    var ok = saveAsWord(html, "Carta_Bienvenida_' + safeId + '.doc");');
      parts.push('    if(!ok){ alert("El navegador bloqueó la descarga. Permite pop-ups/descargas para este sitio."); }');
      parts.push('  });');
      parts.push('})();<\/script>');

      parts.push('</body></html>');
      return parts.join('');
    }

    // ========== 6) Abrir en nueva pestaña ==========
    var w = window.open("about:blank","_blank");
    if (w && !w.closed){
      w.document.open();
      w.document.write(buildLetterHTML());
      w.document.close();
      w.focus();
    } else {
      var blob = new Blob([buildLetterHTML()], {type: "text/html"});
      var url  = URL.createObjectURL(blob);
      location.href = url;
    }

    // IMPORTANTE: devolvemos false para no hacer nada en servidor
    return false;
  }catch(e){
    alert("Error al generar la carta: " + e.message);
    return false;
  }
