beforeJsCode:
try{
  // ===== Utilidades PHPRunner (LIST + EDIT) =====
  var page = (typeof pageObj !== "undefined" && pageObj) ? pageObj : null;

  function limpio(x){
    return (x == null ? "" : String(x).trim());
  }

  // ===== ID DESDE LA FILA DEL LIST =====
  var id = null;

  // PHPRunner nuevo: row.data
  if (row && row.data && typeof row.data["idsolicitud"] !== "undefined"){
    id = row.data["idsolicitud"];
  }
  // Fallback: getFieldValue
  if (!id && row && typeof row.getFieldValue === "function"){
    id = row.getFieldValue("idsolicitud");
  }

  // Fallbacks extra (por si acaso)
  function getIdFromSelection(pg){
    if (!pg || !pg.getSelectedKeys) return null;
    var sel = pg.getSelectedKeys() || [];
    if (!sel.length) return null;
    return sel[0].keys ? sel[0].keys[0] : (sel[0].id || sel[0]);
  }
  function getIdFromPage(pg){
    if (!pg) return null;
    if (pg.getKeys && pg.getKeys().length) return pg.getKeys()[0];
    if (pg.getRecordData){
      var r = pg.getRecordData() || {};
      if (typeof r.idsolicitud !== "undefined") return r.idsolicitud;
    }
    return null;
  }

  if (!id){
    id = getIdFromSelection(page) || getIdFromPage(page);
  }
  if (!id){
    var m = location.search.match(/[?&](editid1|id|key1)=([^&]+)/i);
    if (m) id = decodeURIComponent(m[2]);
  }
  if (!id){
    alert("No se pudo determinar el ID de la solicitud.\nAsegúrate de que 'idsolicitud' está en el SELECT del LIST.");
    return false;
  }

  // ===== Lee valores visibles, priorizando la fila del LIST =====
  function gv(field){
    var v;

    // 1) Desde la fila (row)
    if (row){
      if (row.data && typeof row.data[field] !== "undefined" && row.data[field] !== null && row.data[field] !== ""){
        return String(row.data[field]);
      }
      if (typeof row.getFieldValue === "function"){
        v = row.getFieldValue(field);
        if (v != null && v !== "") return String(v);
      }
    }

    // 2) Desde controles Runner (EDIT o LIST editable)
    try{
      if (window.Runner && Runner.getControl){
        var pidEl = document.querySelector("[data-pageid]");
        var pid = pidEl ? pidEl.getAttribute("data-pageid") : (page && page.id ? page.id : "");
        var c = Runner.getControl(pid, field);
        if (c && typeof c.getValue==="function"){
          var v2 = c.getValue();
          if (v2!=null && v2!=="") return v2;
        }
      }
    }catch(_){}

    // 3) Fallback DOM (primer match)
    var el = document.querySelector('[data-field="'+field+'"]');
    if (el){
      var t = (el.textContent || "").trim();
      if (t) return t;
    }
    return "";
  }

  // ===== Datos del documento (por fila) =====
  var imei = gv("noserie") || "";

  // Leer valor crudo del campo simcard
  var iccRaw = gv("simcard") || "";
  var icc    = "";

  // Limpiar ruidos típicos de la celda del lookup
  if (iccRaw) {
    var txt = String(iccRaw).trim();

    // Si trae el texto del filtro/lookup, lo consideramos vacío
    if ( /Seleccionar todo|resultados de búsqueda|Agregar a la existencia|Borrar todos los filtros|Cancelar/i.test(txt) ) {
      icc = "";
    } else {
      // Si trae varias palabras, nos quedamos con la primera (normalmente el ICC)
      var first = txt.split(/\s+/)[0];
      icc = first;
    }
  }

  var dn = gv("dn") || "";

  // Dirección sugerida (si tienes esos campos en la solicitud)
  var partesDir = [];
  var cCalle  = gv("calle");
  var cNumExt = gv("numeroext");
  var cNumInt = gv("numeroint");
  var cCol    = gv("colonia");
  var cCP     = gv("cp");
  var cCd     = gv("ciudad");
  var cEst    = gv("estado");

  if (cCalle)  partesDir.push(cCalle + (cNumExt ? " " + cNumExt : "") + (cNumInt ? " Int. " + cNumInt : ""));
  if (cCol)    partesDir.push("Col. " + cCol);
  if (cCP)     partesDir.push("C.P. " + cCP);
  if (cCd)     partesDir.push(cCd);
  if (cEst)    partesDir.push(cEst);

  var dirEntrega = partesDir.join(", ");
  var dirEntrega = gv("direccion");

  function fechaLarga(){
    var d = new Date();
    var meses = ['enero','febrero','marzo','abril','mayo','junio','julio','agosto','septiembre','octubre','noviembre','diciembre'];
    return "Mérida, Yucatán a " + d.getDate() + " de " + meses[d.getMonth()] + " de " + d.getFullYear();
  }
  var fechaEnc = fechaLarga();

  function esc(x){ return (x==null ? "" : String(x)); }
  function safeIdPart(x){ return String(x==null?"":x).replace(/[^\w\-]+/g,"_"); }

  // Ruta absoluta para el logo
  function absPath(p){
    var base = location.pathname.substring(0, location.pathname.lastIndexOf("/") + 1);
    return base + p.replace(/^\/+/, "");
  }
  var logoUrl = absPath("images/LOGO NEGRO Y AZUL PEQUE.png");

  // Preparamos el nombre base del archivo Word
  var fnameBase = "Checklist_Entrega" + (id ? "_" + safeIdPart(id) : "");

  // ===== HTML de la vista previa (formato igual al documento) =====
  function buildHTML(){
    var parts = [];
    parts.push('<!doctype html>');
    parts.push('<html lang="es"><head><meta charset="utf-8">');
    parts.push('<title>Checklist Proceso de Entrega</title>');
    parts.push('<meta name="viewport" content="width=device-width, initial-scale=1">');
    parts.push('<style>');
    parts.push('  @page { size: A4; margin: 18mm 18mm 18mm 18mm; }');
    parts.push('  body { font-family: Arial, Helvetica, sans-serif; color:#111; font-size: 11.5pt; }');
    parts.push('  .wrap { max-width: 760px; margin: 0 auto; }');
    parts.push('  .logo{ height: 48px; display:block; }');
    parts.push('  .toolbar { display:flex; gap:10px; justify-content:flex-end; margin:10px 0 14px; }');
    parts.push('  .btn { padding:8px 12px; border:0; border-radius:8px; cursor:pointer; font-weight:700; color:#fff; }');
    parts.push('  .btn-print { background:#111; }');
    parts.push('  .btn-doc   { background:#1565c0; display: none }');
    parts.push('  @media print { .toolbar { display:none; } }');

    // Encabezado (logo izquierda, fecha derecha)
    parts.push('  .hdr { display:flex; justify-content:space-between; align-items:flex-start; margin-bottom: 6px; }');
    parts.push('  .fecha { text-align:right; font-weight:700; }');
    parts.push('  h1 { text-align:center; font-size: 13.5pt; margin: 6px 0 12px 0; font-weight:700; }');

    // Tabla principal
    parts.push('  .grid { width:100%; border-collapse:collapse; table-layout:fixed; }');
    parts.push('  .grid col:first-child { width: calc(100% - 90px); }');
    parts.push('  .grid col:last-child  { width: 90px; }');
    parts.push('  .grid td { padding: 4px 4px; vertical-align: top; }');
    parts.push('  .head-row td { font-weight:700; padding-bottom:8px; border-bottom:1px solid #000; }');
    parts.push('  .head-doc { text-align:left; }');
    parts.push('  .head-ver { text-align:center; }');
    parts.push('  .lbl { }');
    parts.push('  .chk { text-align:center; }');
    parts.push('  .box { display:inline-block; width:12px; height:12px; border:1px solid #111; }');
    parts.push('  .sec-title { font-weight:700; padding-top:10px; }');

    parts.push('  .indent { padding-left: 24px; }');

    // Líneas de texto
    parts.push('  .line { display:inline-block; border-bottom:1px solid #222; min-width: 360px; transform: translateY(-2px); }');
    parts.push('  .line-wide { display:inline-block; border-bottom:1px solid #222; min-width: 580px; transform: translateY(-2px); margin-top: 15px; }');
    parts.push('  .line-mid { display:inline-block; border-bottom:1px solid #222; min-width: 260px; transform: translateY(-2px); }');

    // Firmas (3 columnas)
    parts.push('  .sign-block { margin-top: 24px; }');
    parts.push('  .sign-row3 { display:flex; justify-content:space-between; gap:24px; }');
    parts.push('  .sign-col  { width:33%; text-align:center; }');
    parts.push('  .sign-title { font-weight:600; margin-bottom: 40px; }');
    parts.push('  .sign-hr    { border:0; border-top:1px solid #111; margin: 0 auto 6px; width: 92%; }');
    parts.push('  .sign-name  { font-weight:700; }');
    parts.push('</style></head><body>');
    parts.push('<div class="wrap">');

    // Barra de acciones (vista previa)
    parts.push('  <div class="toolbar">');
    parts.push('    <button class="btn btn-print" onclick="window.print()">Imprimir / PDF</button>');
    parts.push('    <button class="btn btn-doc" id="dlDoc">Descargar Word</button>');
    parts.push('  </div>');

    // Encabezado: logo izquierda + fecha derecha
    parts.push('  <div class="hdr">');
    parts.push('    <img class="logo" src="'+esc(logoUrl)+'" alt="Logo">');
    parts.push('    <div class="fecha">'+esc(fechaEnc)+'</div>');
    parts.push('  </div>');

    // Título
    parts.push('  <h1>CHECKLIST DE PROCESO DE ENTREGA / EN TRANSITO / EXPEDIENTE-CORTE</h1>');

    // Tabla principal (Documentos / Equipos + Verificado)
    parts.push('  <table class="grid"><col><col>');
    parts.push('    <tr class="head-row">');
    parts.push('      <td class="head-doc">Documentos / Equipos</td>');
    parts.push('      <td class="head-ver">Verificado (&#10003;)</td>');
    parts.push('    </tr>');

    // Filas de documentos
    parts.push('    <tr><td class="lbl">CARTA DE BIENVENIDA</td><td class="chk"><span class="box"></span></td></tr>');
    parts.push('    <tr><td class="lbl">CONTRATO</td><td class="chk"><span class="box"></span></td></tr>');
    parts.push('    <tr><td class="lbl">CARTA BURÓ</td><td class="chk"><span class="box"></span></td></tr>');

    parts.push('    <tr><td class="sec-title" colspan="2">RESUMEN DE EQUIPO</td></tr>');
    parts.push('    <tr><td class="lbl indent" >&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;EQUIPO CELULAR COMPLETO Y EN BUEN ESTADO</td><td class="chk"><span class="box"></span></td></tr>');
    parts.push('    <tr><td class="lbl indent" colspan="2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;IMEI DEL EQUIPO:&nbsp;&nbsp;<span class="line">'+esc(imei)+'</span></td></tr>');
    parts.push('    <tr><td class="lbl indent">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SIM</td><td class="chk"><span class="box"></span></td></tr>');
    parts.push('    <tr><td class="lbl indent" colspan="2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ICC ID DE SIM:&nbsp;&nbsp;<span class="line">'+esc(icc)+'</span></td></tr>');
    parts.push('    <tr><td class="lbl indent" colspan="2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;DN:&nbsp;&nbsp;<span class="line">'+esc(dn)+'</span></td></tr>');
    parts.push('    <tr><td class="lbl">FACTURA DE EQUIPO</td><td class="chk"><span class="box"></span></td></tr>');
    parts.push('    <tr><td class="lbl">PÓLIZA DE SEGURO / CARTA DE RECHAZO</td><td class="chk"><span class="box"></span></td></tr>');

    parts.push('    <tr><td class="sec-title" colspan="2">OPCIONES DE IDENTIFICACIÓN</td></tr>');
    parts.push('    <tr><td class="lbl indent">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;INE</td><td class="chk"><span class="box"></span></td></tr>');
    parts.push('    <tr><td class="lbl indent">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;PASAPORTE</td><td class="chk"><span class="box"></span></td></tr>');
    parts.push('    <tr><td class="lbl indent">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FORMA MIGRATORIA</td><td class="chk"><span class="box"></span></td></tr>');
    parts.push('    <tr><td class="lbl">COMPROBANTE DE DOMICILIO</td><td class="chk"><span class="box"></span></td></tr>');

    parts.push('    <tr><td class="lbl" colspan="2" style="padding-top:10px;">DIRECCIÓN DE ENTREGA:&nbsp;<span class="line-wide">'+esc(dirEntrega)+'</span></td></tr>');
    parts.push('    <tr><td class="lbl" colspan="2" style="padding-top:8px;">TOTAL, A PAGAR:&nbsp;<span class="line-mid"></span></td></tr>');

    parts.push('  </table>');

    // Sección de firmas (3 columnas)
    parts.push('  <div class="sign-block">');
    parts.push('    <div class="sign-row3">');
    parts.push('      <div class="sign-col"><div class="sign-title">ENTREGÓ</div></div>');
    parts.push('      <div class="sign-col"><div class="sign-title">RECIBE</div></div>');
    parts.push('      <div class="sign-col"><div class="sign-title">RECEPCIÓN DE EXPEDIENTE Y CORTE</div></div>');
    parts.push('    </div>');
    parts.push('    <div class="sign-row3">');
    parts.push('      <div class="sign-col"><hr class="sign-hr"><div class="sign-name">Ing. Martín Torres Zenteno</div></div>');
    parts.push('      <div class="sign-col"><hr class="sign-hr"><div class="sign-name">Entregador - Validador</div></div>');
    parts.push('      <div class="sign-col"><hr class="sign-hr"><div class="sign-name">Lic. Sol Mora Ortiz</div></div>');
    parts.push('    </div>');
    parts.push('  </div>');

    parts.push('  <div style="text-align:right; font-size:10pt; opacity:.85; margin-top:8px;">Página 1 | 1</div>');
    parts.push('</div>'); // wrap

    // Script DENTRO de la vista previa: handler de #dlDoc
    parts.push('<script>(function(){');
    parts.push('  function saveAsWord(html, filename){');
    parts.push('    if (window.navigator && window.navigator.msSaveOrOpenBlob){');
    parts.push('      var blobIE = new Blob([html], { type: "application/msword;charset=utf-8" });');
    parts.push('      return window.navigator.msSaveOrOpenBlob(blobIE, filename);');
    parts.push('    }');
    parts.push('    try{');
    parts.push('      var blob = new Blob([html], { type: "application/msword;charset=utf-8" });');
    parts.push('      var url  = URL.createObjectURL(blob);');
    parts.push('      var a = document.createElement("a");');
    parts.push('      a.href = url; a.download = filename;');
    parts.push('      document.body.appendChild(a); a.click();');
    parts.push('      setTimeout(function(){ URL.revokeObjectURL(url); if(a.parentNode) a.parentNode.removeChild(a); }, 500);');
    parts.push('      return true;');
    parts.push('    }catch(e){}');
    parts.push('    try{');
    parts.push('      var dataUrl = "data:application/msword;charset=utf-8," + encodeURIComponent(html);');
    parts.push('      var w = window.open(dataUrl, "_blank");');
    parts.push('      if (!w || w.closed || typeof w.closed === "undefined") { location.href = dataUrl; }');
    parts.push('      return true;');
    parts.push('    }catch(e2){ alert("No fue posible generar el Word: " + e2.message); return false; }');
    parts.push('  }');

    parts.push('  function getHTMLForDoc(){');
    parts.push('    var clone = document.documentElement.cloneNode(true);');
    parts.push('    var tb = clone.querySelector(".toolbar"); if (tb && tb.parentNode) tb.parentNode.removeChild(tb);');
    parts.push('    var scripts = clone.querySelectorAll("script");');
    parts.push('    for (var i=0;i<scripts.length;i++){ var s = scripts[i]; if (s.parentNode) s.parentNode.removeChild(s); }');
    parts.push('    return "\\ufeff" + "<!doctype html>\\n" + clone.outerHTML;');
    parts.push('  }');

    parts.push('  var btn = document.getElementById("dlDoc");');
    parts.push('  if(btn){');
    parts.push('    btn.addEventListener("click", function(){');
    parts.push('      try{');
    parts.push('        var html = getHTMLForDoc();');
    parts.push('        var fname = "'+fnameBase+'.doc";');
    parts.push('        var ok = saveAsWord(html, fname);');
    parts.push('        if(!ok){ alert("El navegador bloqueó la descarga. Permite pop-ups/descargas para este sitio."); }');
    parts.push('      }catch(e){ alert("No fue posible generar el Word: " + e.message); }');
    parts.push('    });');
    parts.push('  }');
    parts.push('})();<\/script>');

    parts.push('</body></html>');
    return parts.join('');
  }

  // ===== Abrir vista previa en nueva pestaña y cargar el HTML =====
  var w = window.open("about:blank","_blank");
  if (w && !w.closed){
    w.document.open();
    w.document.write(buildHTML());
    w.document.close();
    w.focus();
  } else {
    // Fallback si bloquean popups: navegar al blob de la vista previa
    var blob = new Blob([buildHTML()], {type:"text/html"});
    var url  = URL.createObjectURL(blob);
    location.href = url;
  }

  // Muy importante: cancelar llamada al servidor
  return false;
}catch(e){
  alert("Error al generar el checklist: " + e.message);
  return false;
}
