beforeJsCode:
// this.recordData tiene los campos de la fila
var idsolicitud = row.getFieldValue("idsolicitud");

if(!idsolicitud){
    alert("No se pudo determinar la solicitud.");
    return;
}

// abre el ADD de la otra tabla en una ventana
var url = "decisionessolicitud_historial_add.php?idsolicitud=" + encodeURIComponent(idsolicitud);
var w = 500;
var h = 300;

// Posición del navegador (soporta varios monitores)
var dualScreenLeft = window.screenLeft !== undefined ? window.screenLeft : window.screenX;
var dualScreenTop  = window.screenTop  !== undefined ? window.screenTop  : window.screenY;

// Tamaño de la ventana del navegador
var width  = window.innerWidth  ? window.innerWidth  : document.documentElement.clientWidth  ? document.documentElement.clientWidth  : screen.width;
var height = window.innerHeight ? window.innerHeight : document.documentElement.clientHeight ? document.documentElement.clientHeight : screen.height;

// Cálculo centrado
var left = dualScreenLeft + (width  - w) / 2;
var top  = dualScreenTop  + (height - h) / 2;

// Abrir popup centrado
var features =
    "width=" + w +
    ",height=" + h +
    ",left=" + left +
    ",top=" + top +
    ",scrollbars=1,resizable=1";

window.open(url, "dec_" + idsolicitud, features);

return false;
