beforeJsCode:
// this.recordData tiene los campos de la fila
var idsolicitud = this.recordData["idsolicitud"];

if(!idsolicitud){
    alert("No se pudo determinar la solicitud.");
    return;
}

// abre el ADD de la otra tabla en una ventana
var url = "solicitudes_decision_add.php?idsolicitud=" + encodeURIComponent(idsolicitud);
window.open(url, "dec_" + idsolicitud, "width=900,height=600,scrollbars=1,resizable=1");
