$page = isset($_SESSION['page']) ? $_SESSION['page'] : 1;
$progress = min(($page - 1) * 33, 100); // Asegura que no pase de 100%

$steps = ['Paso 1', 'Paso 2', 'Paso 3', 'Paso 4'];
$current_step = $page - 1;

$html = <<<EOT
<div class="container mt-4">
    <div class="progress" style="height: 25px;">
        <div class="progress-bar progress-bar-striped progress-bar-animated bg-success" 
             role="progressbar" 
             style="width: {$progress}%" 
             aria-valuenow="{$progress}" 
             aria-valuemin="0" 
             aria-valuemax="100">
            {$progress}% Completado
        </div>
    </div>
    
    <div class="d-flex justify-content-between mt-2">
EOT;

// Agrega los indicadores de pasos
foreach ($steps as $index => $step) {
    $active = ($index < $current_step) ? 'text-success fw-bold' : (($index == $current_step) ? 'text-primary fw-bold' : 'text-muted');
    $html .= "<span class=\"{$active}\">{$step}</span>";
}

$html .= <<<EOT
    </div>
</div>
EOT;

echo $html;
