@import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.0/css/all.min.css');

/* pega en CSS de la página o global */
.panel-collapse.no-bs-trans {
  transition: none !important;
}


/* HOVER global */
body.function-edit .form-control:hover,
body.function-add  .form-control:hover{
  background: #ececec !important;
  border-bottom-color: rgba(0,0,0,.87) !important;
}

/* FOCUS global */
body.function-edit .form-control:focus,
body.function-add  .form-control:focus{

                    border-color: #0366d6;
                    outline: none;
                    box-shadow: rgba(3, 102, 214, 0.3) 0px 0px 0px 3px;
}

/* Evita que afecte a checkbox/radio/file/range */
body.function-edit input.form-control[type=checkbox],
body.function-edit input.form-control[type=radio],
body.function-edit input.form-control[type=file],
body.function-edit input.form-control[type=range],
body.function-add  input.form-control[type=checkbox],
body.function-add  input.form-control[type=radio],
body.function-add  input.form-control[type=file],
body.function-add  input.form-control[type=range]{
  background: transparent !important;
  border: none !important;
}

.form-control:disabled,
.form-control[readonly]{
  background: #eeeeee !important;
  border-bottom-color: rgba(0,0,0,.12) !important;
  color: rgba(0,0,0,.45) !important;
  cursor: not-allowed;
}




/************************************************************/

/* ---- Configura tus tonos ---- */

:root{
  --accent: #FF4500; /*#7B68EE;   */      /* morado oscuro */
  --accent-hover: #FFD700;   /* morado al pasar el mouse */
}

/* Listas/Lookups hechos con tablas Bootstrap */
.table > thead > tr > th a:not(.btn),
/*.table > tbody > tr > td a:not(.btn),*/
.table > tfoot > tr > td a:not(.btn) {
  color: var(--accent) !important;
}
.table > thead > tr > th a:not(.btn):hover,
/*.table > tbody > tr > td a:not(.btn):hover,*/
.table > tfoot > tr > td a:not(.btn):hover {
  color: var(--accent-hover) !important;
}

/* Por si la grid no usa .table en alguna página */
.r-grid table a:not(.btn) {
  color: var(--accent) !important;
}
.r-grid table a:not(.btn):hover {
  color: var(--accent-hover) !important;
}



