  var CAGE_WIDTH  = 820; // ancho total del "card"
  var FIELD_WIDTH = 420; // ancho máx. de controles

  // Marca la página para los estilos
  $('body').addClass('addons-narrow');

  function cageOnce() {
    var $page = $('[data-page="addons_add"]');
    if (!$page.length) return;

    // Encapsula top, grid (form) y bottom en "cages" SOLO si aún no lo están
    [
      ['.r-form[data-location="top"]',        'addons-cage-top'],
      ['.r-form[data-location="grid"]',       'addons-cage'],
      ['.r-form[data-location="below-grid"]', 'addons-cage-bot']
    ].forEach(function(pair){
      var sel = pair[0], cls = pair[1];
      var $blk = $page.find(sel).first();
      if ($blk.length && !$blk.parent().hasClass(cls)) {
        $blk.wrap('<div class="'+cls+'"></div>');
      }
    });
  }

  // 1) Inicial
  cageOnce();

  // 2) Tras pequeños cambios/animaciones iniciales
  setTimeout(cageOnce, 120);

  // 3) Observa el body por si PHPRunner reinyecta el form
  new MutationObserver(function(muts){
    for (var i=0;i<muts.length;i++){
      var m = muts[i];
      if (m.addedNodes && m.addedNodes.length){
        cageOnce();
        break;
      }
    }
  }).observe(document.body, {childList:true, subtree:true});

  // 4) Ganchos extra (por si usas callbacks personalizados)
  //   Llama a $(document).trigger('addons:cage') cuando guardes/recargues manualmente
  $(document).on('addons:cage', cageOnce);
})();

