body.function-add #form_below-grid_1 .rnr-buttons-group {
  display: inline-flex;    /* mantiene los botones en l√≠nea */
  gap: 8px;  /* separaci√≥n entre botones */
  justify-content: flex-start;  
  flex-wrap: wrap;         /* por si colapsan en pantallas chicas */
}

/* Por si alg√∫n tema pone floats o pull-right */
