// =========================================================================
// 1. CONFIGURACI√ìN Y VARIABLES
// =========================================================================

// [OPTIMIZACI√ìN 1: Timezone] Mover date_default_timezone_set a la configuraci√≥n global de PHPRunner (events/global/AfterSuccessfulLogin.php)
// $values['fechaHora'] = date('Y-m-d H:i:s'); // Mantener si el campo es de texto. Si es DateTime, mejor usar NOW() en SQL.

$simcard = $values['SIMEntregado'];
$idsolicitud = (int)$values['idsolicitud']; // Asegurar que es un entero desde el inicio.
$tienda_vnp = (int)$_SESSION["tiendaNP"]; // Asumir que es entero, tipar a (int).

// =========================================================================
// 2. HELPERS (Mantener)
// =========================================================================
// Se eliminan los helpers no utilizados.

function q($v){ // cadenas/fechas: 'valor' o NULL
    return (isset($v) && $v !== '' ? db_prepare_string($v) : "NULL");
}
function qi($v){ // enteros: n√∫mero o NULL
    return (isset($v) && $v !== '' ? (int)$v : "NULL");
}

// Variables citadas para SQL
$simcard_quoted = q($simcard);


// =========================================================================
// 3. CONSULTA DE INVENTARIO (Estandarizaci√≥n y Robustez)
// =========================================================================

$strSQLExists="
    SELECT
        compras.noserie
    FROM compras
    LEFT OUTER JOIN ventas v ON compras.noserie = v.esnimei
    LEFT OUTER JOIN movimientos m ON compras.noserie = m.esnimei
    LEFT OUTER JOIN (
        SELECT t.esnimei, t.tiendadestino, t.fecharecepcion
        FROM traspasosdet t 
        INNER JOIN (SELECT MAX(idtraspasodet) idtraspaso FROM traspasosdet GROUP BY esnimei) t1 
            ON t.idtraspasodet = t1.idtraspaso
    ) t ON compras.noserie = t.esnimei
    INNER JOIN productos ON compras.codigoproducto = productos.codigoIUSA
    WHERE 
        v.esnimei IS NULL 
        AND m.esnimei IS NULL  
        AND (t.esnimei IS NULL OR t.fecharecepcion IS NOT NULL AND t.fecharecepcion <> '0000-00-00')
        AND compras.tienda = {$tienda_vnp} 
        AND compras.clasif = 2 
        AND productos.Clasif = 'Chip' 
        AND compras.noserie = {$simcard_quoted}
    LIMIT 1
";

// [OPTIMIZACI√ìN 2: Uso de DB::Query y manejo de error]
$result = DB::Query($strSQLExists);

// CR√çTICO: Verificar si la consulta SELECT fall√≥ por error de BD
if (!$result) {
    $message = "Error de base de datos al verificar inventario: " . DB::LastError();
    error_log($message, 3, "debug_log.log");
    return false;
}
$data = $result->fetchAssoc();

if (!$data) {
    // FALLO: No existe saldo para el n√∫mero de serie.
    $message="No existe saldo en el inventario para el n√∫mero de serie";
    return false;
} else {
    // La verificaci√≥n SQL ya confirm√≥ que $simcard existe en inventario

    // 4a. DELETE previo (siempre se ejecuta)
    // [OPTIMIZACI√ìN 3: Eliminar $conn y usar chequeo de ejecuci√≥n]
    $sql_delete = "DELETE FROM seriessolicitadas WHERE esnimei = {$simcard_quoted}";
    if (!DB::Exec($sql_delete)) {
        // No es fatal, solo se loguea, ya que el registro puede no existir
        error_log("Error al eliminar serie solicitada: " . DB::LastError(), 3, "debug_log.log");
    }

    // 4b. INSERT en seriessolicitadas
    // [OPTIMIZACI√ìN 4: Simplificaci√≥n de sprintf a interpolaci√≥n de cadenas]
    $sql_insert = "INSERT INTO seriessolicitadas (idsolicitud, esnimei) VALUES ({$idsolicitud}, {$simcard_quoted})";

    if (!DB::Exec($sql_insert)) {
        // FALLO: No se pudo guardar el equipo.
        $message = "No se pudo guardar el equipo en series solicitadas: ".DB::LastError();
        return false;
    }  
}

return true;

