  // ================== Setear DATETIME robusto en fila inline ==================
  // isoDate: 'YYYY-MM-DD' | hhmm: 'HH:MM' o 'HH:MM:SS' (opcional, default '00:00')
  window.setInlineDatePHR = function(rowEl, fieldName, isoDate, hhmm){
    hhmm = hhmm || '00:00';
    var hasSS = /^\d{2}:\d{2}:\d{2}$/.test(hhmm);
    var hh = (hhmm.split(':')[0] || '00'), mm = (hhmm.split(':')[1] || '00'), ss = hasSS ? hhmm.split(':')[2] : '00';
    var isoDT = isoDate + ' ' + hh + ':' + mm + ':' + ss;

    // 1) Runner control (Date/DateTime)
    try {
      var pid  = ($('[data-pageid]').first().attr('data-pageid') || '');
      var rid  = ($(rowEl).attr('data-rowid') || $(rowEl).attr('data-record-id') || '');
      var ctrl = (window.Runner && Runner.getControl) ? Runner.getControl(pid, fieldName, rid) : null;
      if (ctrl && typeof ctrl.setValue === 'function'){
        ctrl.setValue(isoDT);
        if (typeof ctrl.fireEvent === 'function') ctrl.fireEvent('change');
        return true;
      }
    } catch(e){}

    // 2) datetime-local
    var $dtl = $(rowEl).find('[id^="value_'+fieldName+'_"]').filter('input[type="datetime-local"]').first();
    if ($dtl.length){
      $dtl.val(isoDate + 'T' + hh + ':' + mm).trigger('change');
      return true;
    }

    // 3) date + time
    var $date = $(rowEl).find('[id^="value_'+fieldName+'_"]').filter('input[type="date"]').first();
    var $time = $(rowEl).find('[id^="value_'+fieldName+'_"]').filter('input[type="time"],input.timepicker').first();
    if ($date.length){
      $date.val(isoDate).trigger('change');
      if ($time.length){ $time.val(hh + ':' + mm + (hasSS? (':' + ss) : '')).trigger('change'); }
      return true;
    }

    // 4) select por partes
    var $y = $(rowEl).find('[id^="value_'+fieldName+'_"][id$="_year"]');
    var $m = $(rowEl).find('[id^="value_'+fieldName+'_"][id$="_month"]');
    var $d = $(rowEl).find('[id^="value_'+fieldName+'_"][id$="_day"]');
    var $H = $(rowEl).find('[id^="value_'+fieldName+'_"][id$="_hour"]');
    var $M = $(rowEl).find('[id^="value_'+fieldName+'_"][id$="_minute"]');
    var $S = $(rowEl).find('[id^="value_'+fieldName+'_"][id$="_second"]');
    if ($y.length && $m.length && $d.length){
      $y.val(isoDate.slice(0,4)).trigger('change');
      $m.val(String(parseInt(isoDate.slice(5,7),10))).trigger('change');
      $d.val(String(parseInt(isoDate.slice(8,10),10))).trigger('change');
      if ($H.length) $H.val(hh).trigger('change');
      if ($M.length) $M.val(mm).trigger('change');
      if ($S.length) $S.val(ss).trigger('change');
      return true;
    }

    // 5) fallback: input text
    var $txt = $(rowEl).find('[id^="value_'+fieldName+'_"]').filter('input[type="text"]').first();
    if ($txt.length){ $txt.val(isoDT).trigger('change'); return true; }

    return false;
  };

  // ================== Check redondo sincronizado ==================
  function enhanceCell(td, field, rowId, offChar){
    if (!td || td.__prettyMounted) return;
    td.__prettyMounted = true;

    var $ck = $(td).find('input[type="checkbox"]');
    var $hidden = null;

    if (!$ck.length){
      // si PHPRunner muestra 0/1 como texto, crea un hidden para conservar valor en submit
      var current = ($(td).text() || '').trim();
      var init = (current === '1' || /si|s√≠|true/i.test(current));
      $hidden = $('<input type="hidden">').val(init ? '1' : '0').appendTo(td);
    } else {
      // oculta el checkbox real pero lo deja para el submit
      $ck.css({position:'absolute', opacity:0, pointerEvents:'none', width:0, height:0});
    }

    function read(){ return $ck.length ? !!$ck.prop('checked') : ($hidden && $hidden.val() === '1'); }
    function write(val){
      if ($ck.length){ $ck.prop('checked', !!val).trigger('change'); }
      else if ($hidden){ $hidden.val(val ? '1' : '0').trigger('change'); }
      else {
        var c = getCtrl(field, rowId);
        if (c && typeof c.setValue === 'function') c.setValue(val ? 1 : 0);
      }
    }

    var checked = read();
    var btn = document.createElement('button');
    btn.type = 'button';
    btn.className = 'check-animation ' + (checked ? 'bg-green-500' : 'bg-gray-300');
    btn.textContent = checked ? '‚úì' : (offChar || '‚Ä¢');

    btn.addEventListener('click', function(){
      checked = !checked; write(checked);
      btn.classList.toggle('bg-green-500', checked);
      btn.classList.toggle('bg-gray-300', !checked);
      btn.textContent = checked ? '‚úì' : (offChar || '‚Ä¢');
      btn.classList.add('bounce-in');
      setTimeout(function(){ btn.classList.remove('bounce-in'); }, 320);
    });

    // limpia contenido crudo y coloca el bot√≥n centrado
    $(td).contents().wrapAll('<span class="pc-hidden" style="display:none"></span>');
    var holder = document.createElement('div');
    holder.style.display='flex'; holder.style.justifyContent='center'; holder.style.alignItems='center';
    holder.appendChild(btn); td.appendChild(holder);
  }

  // ================== Iconos en encabezados ==================
  function iconizeHeaders(){
    var scope = 'table, .rnr-b-grid table, .details-grid table, .rnr-grid-block table';
    (window.CHECK_FIELDS || []).forEach(function(it){
      var field = it.name, icon = it.icon;
      var $th = $(scope+' thead th[data-field="'+field+'"]').first();
      if (!$th.length) $th = $(scope+' thead th:has([data-field="'+field+'"])').first();
      if (!$th.length) $th = $(scope+' thead th[id*="'+field+'"], '+scope+' thead th[aria-label*="'+field+'"]').first();
      if ($th.length && !$th.hasClass('th-iconized')){
        $th.addClass('th-iconized').attr('data-icon', icon);
      }
    });
  }

  // ================== Embellecer filas ==================
  function beautifyRow(rowId){
    var $tr = $('tr[data-rowid="'+rowId+'"], tr[data-record-id="'+rowId+'"]');
    if (!$tr.length) return;
    (window.CHECK_FIELDS || []).forEach(function(it){
      var td = $tr.find('td[data-field="'+it.name+'"]').get(0) || $tr.find('td[id*="'+it.name+'"]').get(0);
      if (td) enhanceCell(td, it.name, rowId, it.offChar || it.icon);
    });
  }
  function beautifyAll(){
    $('tr[data-rowid], tr[data-record-id]').each(function(){
      var rid = $(this).attr('data-rowid') || $(this).attr('data-record-id');
      if (rid) beautifyRow(rid);
    });
    iconizeHeaders();
  }

  // inicial y re-aplicaci√≥n por cambios en la grid
  setTimeout(beautifyAll, 150);
  var root = document.querySelector('.r-grid') || document.body;
  new MutationObserver(function(muts){
    var touched = muts.some(function(m){ return m.addedNodes && m.addedNodes.length; });
    if (touched) setTimeout(beautifyAll, 60);
  }).observe(root, {childList:true, subtree:true});

  // ================== (OPCIONAL) Helper para tu Quick Add ==================
  // Si desde tu tarjeta/ bot√≥n ‚ÄúAgregar‚Äù obtienes la fila inline como <tr> (row),
  // llama a esto para fijar fecha y, si quieres, el SIM:
  window.fillInlineLogistica = function(row, fechaISO /* 'YYYY-MM-DD' */, hhmm /* 'HH:MM' opcional */, simVal){
    window.setInlineDatePHR(row, DATE_FIELD, fechaISO, hhmm || '00:00');
    if (simVal != null){
      var si = row.querySelector('[id^="value_'+SIM_FIELD+'_"]');
      if (si){ si.value = String(simVal); si.dispatchEvent(new Event('input',{bubbles:true})); si.dispatchEvent(new Event('change',{bubbles:true})); }
    }
  };
