(function(){
  // === Campos a embellecer (ajusta nombres si tus campos tienen otros) ===
  const CHECK_FIELDS = [
    { name: 'lista_entregada', icon: 'üìã' },
    { name: 'manual',          icon: 'üìñ' },
    { name: 'contrato',        icon: 'üìÑ' }
  ];

  // Helper: pageid y control por fila
  function getPid(){ return $('[data-pageid]').first().attr('data-pageid') || ''; }
  function getCtrl(field, rowId){
    try { if (window.Runner && typeof Runner.getControl === 'function') {
      return Runner.getControl(getPid(), field, rowId);
    }} catch(e){}
    return null;
  }

  // Lee y escribe valor del campo (soporta checkbox o texto 0/1/true/false)
  function readField(field, rowId){
    const c = getCtrl(field, rowId);
    if (c && typeof c.getValue === 'function') return c.getValue();
    // fallback DOM
    const pid = getPid();
    const $el = $(`#value_${field}_${pid}_${rowId}, #display_value_${field}_${pid}_${rowId}`);
    if (!$el.length) return '';
    const v = $el.is(':checkbox') ? $el.prop('checked') : ($el.val() || $el.text() || '').trim();
    if (v === true || v === false) return v;
    return (v === '1' || v === 'true' || v === 'SI' || v === 'S√≠');
  }
  function writeField(field, rowId, val){
    const c = getCtrl(field, rowId);
    if (c && typeof c.setValue === 'function') { c.setValue(val ? 1 : 0); return; }
    // fallback DOM
    const pid = getPid();
    const $el = $(`#value_${field}_${pid}_${rowId}, #display_value_${field}_${pid}_${rowId}`);
    if ($el.length){
      if ($el.is(':checkbox')) $el.prop('checked', !!val).trigger('change');
      else $el.val(val ? '1' : '0').trigger('change');
    }
  }

  // Dibuja el bot√≥n y sincroniza
  function mountPrettyCheck(td, field, rowId, icon){
    if (!td || td.__prettyMounted) return;
    td.__prettyMounted = true;

    // estado inicial
    let checked = !!readField(field, rowId);

    // ocultar contenido crudo
    const $raw = $(td).contents().wrapAll('<div class="hidden"></div>');

    // crear bot√≥n
    const btn = document.createElement('button');
    btn.type = 'button';
    btn.className =
      'check-animation relative w-8 h-8 rounded-full flex items-center justify-center '+
      'text-white font-bold shadow-lg hover:shadow-xl mx-auto';
    btn.title = field;

    function paint(){
      btn.classList.remove('bg-gray-300','bg-green-500');
      btn.classList.add(checked ? 'bg-green-500' : 'bg-gray-300');
      btn.textContent = checked ? '‚úì' : icon;
    }
    paint();

    btn.addEventListener('click', function(){
      checked = !checked;
      writeField(field, rowId, checked);
      paint();
      // efecto ‚Äúbounce‚Äù
      btn.classList.add('bounce-in');
      setTimeout(()=>btn.classList.remove('bounce-in'), 400);
    });

    // insertar
    const holder = document.createElement('div');
    holder.className = 'flex items-center justify-center py-1';
    holder.appendChild(btn);
    td.appendChild(holder);
  }

  // Embellece una fila (por rowId)
  function beautifyRow(rowId){
    // ubicar <tr> de esa fila
    const $tr = $(`tr[data-rowid="${rowId}"], tr[data-record-id="${rowId}"]`);
    if (!$tr.length) return;

    CHECK_FIELDS.forEach(({name, icon})=>{
      // localizar celda por data-field o √≠ndice
      let td = $tr.find(`td[data-field="${name}"]`)[0];
      if (!td) {
        // fallback: busca por id parcial
        td = $tr.find(`td[id*="${name}"]`)[0];
      }
      if (td) mountPrettyCheck(td, name, rowId, icon);
    });
  }

  // Detectar filas y aplicar
  function beautifyAll(){
    $('tr[data-rowid], tr[data-record-id]').each(function(){
      const rowId = $(this).attr('data-rowid') || $(this).attr('data-record-id');
      if (rowId) beautifyRow(rowId);
    });
  }

  // 1) Al inicio
  setTimeout(beautifyAll, 200);

  // 2) Cuando una fila entra en edici√≥n en l√≠nea (PHPRunner dibuja controles)
  //    Hook por mutaciones de la grid
  const root = document.querySelector('.r-grid') || document.body;
  new MutationObserver(function(muts){
    muts.forEach(m=>{
      m.addedNodes && m.addedNodes.forEach(n=>{
        if (!(n instanceof HTMLElement)) return;
        // si agregan una fila o re-renderizan celdas
        if (n.matches('tr[data-rowid], tr[data-record-id]')) {
          const rid = n.getAttribute('data-rowid') || n.getAttribute('data-record-id');
          beautifyRow(rid);
        } else if (n.querySelectorAll) {
          n.querySelectorAll('tr[data-rowid], tr[data-record-id]').forEach(tr=>{
            const rid = tr.getAttribute('data-rowid') || tr.getAttribute('data-record-id');
            beautifyRow(rid);
          });
        }
      });
    });
  }).observe(root, { childList:true, subtree:true });

  // 3) (Opcional) marcas visuales m√≠nimas de tabla (como en tu canvas)
  $('table').addClass('w-full');
  $('thead').addClass('bg-gradient-to-r from-purple-500 to-pink-500 text-white');
})();
