// ====================== HELPERS GENERALES ======================
function currentPageId() {
  if (typeof window.pageid !== 'undefined' && window.pageid) return window.pageid;
  var pid = $('[data-pageid]').first().attr('data-pageid');
  return pid || '';
}
function getCtrl(field) {
  try { return Runner.getControl(currentPageId(), field); } catch(e) { return null; }
}
function readVal(field) {
  var c = getCtrl(field);
  if (c && typeof c.getValue === 'function') return c.getValue();
  var pid = currentPageId();
  var $id = $('#value_'+field+'_'+pid);
  if ($id.length) return $id.val();
  var $w = $('div.r-integrated-field[data-field="'+field+'"][data-pageid="'+pid+'"]');
  if ($w.length) {
    var $inp = $w.find('input,select,textarea').first();
    if ($inp.length) return $inp.val();
  }
  var $n = $('[name="'+field+'"]');
  if ($n.length) return $n.val();
  return '';
}
function setVal(field, value, fireChange) {
  var c = getCtrl(field);
  if (c && typeof c.setControlValue === 'function') {
    c.setControlValue(value);
    if (fireChange && typeof c.fireEvent === 'function') c.fireEvent('change');
    return true;
  }
  var pid = currentPageId();
  var $id = $('#value_'+field+'_'+pid);
  if ($id.length) {
    $id.val(value).trigger('change');
    if (fireChange) $id.trigger('keyup');
    return true;
  }
  var $w = $('div.r-integrated-field[data-field="'+field+'"][data-pageid="'+pid+'"]');
  if ($w.length) {
    var $inp = $w.find('input,select,textarea').first();
    if ($inp.length) {
      $inp.val(value).trigger('change');
      if (fireChange) $inp.trigger('keyup');
      return true;
    }
  }
  return false;
}
function setNoSerieValue(noserie) {
  var ok = setVal('noserie', noserie, true);
  if (ok) return;
  var tries = 0, max = 40, delay = 150;
  (function retry(){
    tries++;
    if (setVal('noserie', noserie, true)) return;
    if (tries >= max) return;
    setTimeout(retry, delay);
  })();
}
function hasErrorMessage() {
  if (window.FORM_ERROR === true) return true;
  var $msg = $('[data-itemtype="edit_message"].alert, .alert[data-itemtype="edit_message"]');
  if (!$msg.length) return false;
  var txt = $.trim($msg.text() || '');
  if (!txt) return false;
  if ($msg.is('.alert-danger, .alert-error')) return true;
  return true;
}
function getQS(name){
  var m = new RegExp('[?&]'+name+'=([^&]*)').exec(location.search);
  return m ? decodeURIComponent(m[1].replace(/\+/g,' ')) : null;
}
function hasSuccessOrClean() {
  var okMsg = $('.alert.alert-success[data-itemtype="edit_message"], [data-itemtype="edit_message"].alert-success');
  if (okMsg.length) return true;
  return (getQS('clean') === '1');
}

// --- limpieza fuerte tras √©xito/entrada limpia ---
function resetAllFieldsUI() {
  window.__recovering = true;
  // 1¬™ pasada
  (window.FIELDS || []).forEach(function (f) { setVal(f, '', true); });
  window.__res_noserie = null;
  try { sessionStorage.removeItem(window.STORAGE_KEY); } catch(e){}

  // 2¬™ pasada (despu√©s del pintado)
  setTimeout(function(){
    (window.FIELDS || []).forEach(function (f) { setVal(f, '', true); });
    window.__res_noserie = null;
    try { sessionStorage.removeItem(window.STORAGE_KEY); } catch(e){}
    setTimeout(function(){ window.__recovering = false; }, 150);
  }, 220);
}

// loop de refuerzo por si PHPRunner hidrata tarde
function startForceClearLoop(ms) {
  var end = Date.now() + (ms || 1000);
  (function loop(){
    if (Date.now() > end) return;
    (window.FIELDS || []).forEach(function (f) { setVal(f, '', false); });
    setTimeout(loop, 120);
  })();
}

// ====================== AUTOSAVE ======================
function buildDraft() {
  var d = {};
  for (var i=0;i<window.FIELDS.length;i++){
    var f = window.FIELDS[i];
    var v = readVal(f);
    if (v !== null && v !== undefined && v !== '') d[f] = v;
  }
  d.timestamp = Date.now();
  return d;
}
function saveDraftNow() {
  try { sessionStorage.setItem(window.STORAGE_KEY, JSON.stringify(buildDraft())); } catch(e){}
}
function makeDebounce(fn, ms){
  var timeoutId = 0;
  return function(){
    var ctx = this, args = arguments;
    if (timeoutId) { try { window.clearTimeout(timeoutId); } catch(e){} }
    timeoutId = window.setTimeout(function(){
      timeoutId = 0;
      try { fn.apply(ctx, args); } catch(e){}
    }, ms || 200);
  };
}
var saveDraftDebounced = makeDebounce(saveDraftNow, 300);

// ====================== Salida fiable ======================
function safeReleaseAndClear() {
  try {
    if (window.__res_noserie) {
      try {
        navigator.sendBeacon(
          window.ENDPOINT,
          new URLSearchParams({ action:'release', noserie: window.__res_noserie })
        );
      } catch(e) {}
      window.__res_noserie = null;
    }
    sessionStorage.removeItem(window.STORAGE_KEY);
  } catch(e) {}
}

// BFCache: si vuelve de cach√© y NO hay borrador, limpia inputs
window.addEventListener('pageshow', function (event) {
  if (!event.persisted) return;
  if (sessionStorage.getItem(window.STORAGE_KEY)) return;
  (window.FIELDS || []).forEach(function (f) { setVal(f, '', true); });
  window.__res_noserie = null;
  window.__recovering = false;
});

// Salida ‚Üí no limpiar si es submit (para conservar datos si falla)
window.addEventListener('pagehide', function(){
  if (!window.__submitting) safeReleaseAndClear();
});
document.addEventListener('visibilitychange', function(){
  if (document.visibilityState === 'hidden' && !window.__submitting) {
    safeReleaseAndClear();
  }
});
window.addEventListener('beforeunload', function(){
  saveDraftNow();
  if (!window.__submitting && window.__res_noserie) {
    try {
      navigator.sendBeacon(
        window.ENDPOINT,
        new URLSearchParams({ action:'release', noserie: window.__res_noserie })
      );
    } catch(e) {}
  }
});

// ====================== RESERVA POR PRODUCTO ======================
function releasePrev() {
  if (!window.__res_noserie) return;
  $.post(window.ENDPOINT, { action:'release', noserie: window.__res_noserie }, function(){}, 'json');
  window.__res_noserie = null;
}
function reserveForProduct(productId){
  $.post(window.ENDPOINT, { action:'reserve_by_product', product_id: productId }, null, 'json')
    .done(function(resp){
      var r = (typeof resp === 'object') ? resp : (function(){ try{return JSON.parse(resp);}catch(e){return {};}})();
      if (r && r.ok) {
        setNoSerieValue(r.noserie);
        window.__res_noserie = r.noserie;
        saveDraftNow();
      } else if (r && r.error === 'no-stock') {
        alert('No hay series disponibles para este producto.');
        setVal('idproducto','',true);
        setNoSerieValue('');
        releasePrev();
        saveDraftNow();
      } else {
        alert('No se pudo reservar la serie. Intenta de nuevo.');
        setNoSerieValue('');
        saveDraftNow();
      }
    })
    .fail(function(){
      alert('Error de comunicaci√≥n al reservar la serie.');
      setNoSerieValue('');
      saveDraftNow();
    });
}

// ====================== INICIO DIFERIDO ======================

// === HARD RESET tras guardado exitoso ===
// Llama esto lo m√°s pronto posible en tu onload (antes o al inicio del setTimeout)
(function hardResetIfNeeded(){
  // Se setea en BeforeProcessAdd o viene por ?clean=1
  var clean = !!(window.CLEAN_ON_LOAD) || /[?&]clean=1(?:&|$)/i.test(location.search);
  if (!clean) return;

  // Evita que autosave vuelva a meter datos mientras limpiamos
  window.__recovering = true;
  window.SKIP_RECOVERY = true;
  try { sessionStorage.removeItem(window.STORAGE_KEY); } catch(e){}

  // 1) Reseteo nativo del <form> (limpia inputs HTML)
  var $form = $('form').first();
  if ($form.length && $form[0].reset) { try { $form[0].reset(); } catch(e){} }

  // 2) Dos pasadas sobre Runner controls (despu√©s del montaje)
  function clearAllOnce() {
    (window.FIELDS || []).forEach(function(f){
      // limpia Runner control si existe, si no, el input nativo
      var c = (function(){ try { return Runner.getControl(currentPageId(), f); } catch(e){ return null; }})();
      if (c && typeof c.setControlValue === 'function') {
        try { c.setControlValue(''); if (c.fireEvent) c.fireEvent('change'); } catch(e){}
      } else {
        setVal(f, '', true);
      }
    });
    window.__res_noserie = null;
  }

  // primera pasada r√°pida
  clearAllOnce();

  // segunda pasada con peque√±o delay (evita rehidrataci√≥n tard√≠a)
  setTimeout(clearAllOnce, 220);

  // 3) loop de refuerzo por ~1 segundo
  var tEnd = Date.now() + 1000;
  (function loop(){
    if (Date.now() > tEnd) {
      // reactivamos autosave
      setTimeout(function(){ window.__recovering = false; }, 120);
      return;
    }
    clearAllOnce();
    setTimeout(loop, 120);
  })();
})();




setTimeout(function(){
  var pid      = currentPageId();
  var ctrlProd = getCtrl('idproducto');
  if (!ctrlProd) return;

  // 0) MARCAR SUBMIT UNIVERSAL
  $(document).on('submit', 'form', function(){
    window.__submitting = true;
    saveDraftNow();
  });
  var SAVE_SELECTOR = "[data-itemtype='edit_save'] a, [data-itemid='edit_save'] a, a#saveButton1, button#saveButton1";
  $(document).on('click', SAVE_SELECTOR, function(){
    window.__submitting = true;
    saveDraftNow();
  });

  // üöÄ MODO LIMPIEZA: si hay √©xito, si CLEAN_ON_LOAD=true (inyectado en PHP), o si SKIP_RECOVERY y NO hay error
  window.CLEAN_ON_LOAD = window.CLEAN_ON_LOAD || (getQS('clean') === '1');
  var cleanMode = hasSuccessOrClean() || !!window.CLEAN_ON_LOAD || (window.SKIP_RECOVERY && !hasErrorMessage());

  if (cleanMode) {
    try { sessionStorage.removeItem(window.STORAGE_KEY); } catch(e){}
    window.SKIP_RECOVERY = true;
    resetAllFieldsUI();
    startForceClearLoop(1000); // refuerzo ~1s
  } else {
    // 1) RECUPERACI√ìN DESDE sessionStorage (si hay, no caduc√≥ y no hay error)
    var draftJson = sessionStorage.getItem(window.STORAGE_KEY);
    if (draftJson && !window.SKIP_RECOVERY && !hasErrorMessage()) {
      try {
        var draft = JSON.parse(draftJson) || {};
        var now   = Date.now();
        if (draft.timestamp && (now - draft.timestamp > MAX_DRAFT_AGE_MS)) {
          sessionStorage.removeItem(window.STORAGE_KEY);
        } else {
          window.__recovering = true;
          if (draft.idproducto != null && draft.idproducto !== '') {
            setVal('idproducto', draft.idproducto, true);
          }
          Object.keys(draft).forEach(function(k){
            if (k === 'timestamp' || k === 'idproducto') return;
            setVal(k, draft[k], false);
          });
          if (draft.noserie) window.__res_noserie = draft.noserie;
          sessionStorage.removeItem(window.STORAGE_KEY);
          setTimeout(function(){ window.__recovering = false; }, 150);
        }
      } catch(e){
        console.error('Borrador inv√°lido en sessionStorage:', e);
        sessionStorage.removeItem(window.STORAGE_KEY);
      }
    } else if (window.SKIP_RECOVERY) {
      sessionStorage.removeItem(window.STORAGE_KEY);
    }
  }

  // 2) CAMBIO DE PRODUCTO ‚Üí RESERVA
  ctrlProd.on('change', function(){
    var productId = ctrlProd.getValue();
    if (!productId) return;
    if (window.__recovering) return;

    var prevSerie = readVal('noserie');
    if (prevSerie && window.__res_noserie === prevSerie) {
      $.post(window.ENDPOINT, { action:'release', noserie: window.__res_noserie }, function(){}, 'json');
      window.__res_noserie = null;
    } else {
      releasePrev();
    }
    setNoSerieValue('');
    reserveForProduct(productId);
  });

  // 3) AUTOGUARDADO (timer + por cambios)
  setInterval(function(){ if (!window.__recovering) saveDraftNow(); }, 5000);

  var selDeleg = window.FIELDS.map(function(f){
    return 'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pid+'"] input, '+
           'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pid+'"] select, '+
           'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pid+'"] textarea';
  }).join(', ');
  $(document).on('change input', selDeleg, makeDebounce(saveDraftNow, 300));

  // 4) SALIR (cualquier step)
  (function(){
    var BACK_SELECTOR = [
      '#backButton1',
      '#backButton2',
      "[data-itemtype='edit_back_list'] a",
      "[data-itemid='edit_back_list'] a",
      "[data-itemtype='edit_close'] a",
      "[data-itemid='edit_close'] a",
      "a[href$='solicitudes_list.php']",
      "a[href*='solicitudes_list.php?']"
    ].join(', ');
    $(document).on('mousedown click', BACK_SELECTOR, function(){
      window.__submitting = false;
      safeReleaseAndClear();
    });
  })();

}, 500);

