        $extras[$f] = $all_values[$f];

        // La clave: quitar los campos de la tabla principal ($values)
        // PERO conservar el valor en $all_values para guardarlo si falla.
        if (array_key_exists($f, $values)) {
            unset($values[$f]); 
        }
    } else {
        // Si es un "Custom field" o no est√° en $values (viene directo del POST)
