/*
(function () {
  // ============ CONFIG ============
  const CONFIG = {
    // Lista completa de campos que quieres gobernar (exactos)
    fields: ['estatus','estatusentrega'],

    // Aliases para roles num√©ricos -> texto (opcional)
    roleAliases: { '1':'ADMIN', '2':'VALIDADOR' },

    // Reglas: la PRIMERA que coincida se aplica ("first match wins").
    // Usa enable/readonly/disable con arrays de campos o '*' para todos los de CONFIG.fields
    rules: [
      // Admin: todo habilitado
      { match: { role: ['ADMIN'] }, enable: ['*'] },

      // Validador: puede editar estatus; lo dem√°s deshabilitado
      { match: { role: ['VALIDADOR'] }, enable: ['estatus'], disable: ['*'] },

      // Overrides por usuario (ejemplo): solo lectura de comentarios
      { match: { user: ['VENDEDORES','GERENTES'] }, disable: ['*'] },

      // Por defecto: todo deshabilitado
      { match: { default: true }, disable: ['*'] }
    ]
  };
  // ========== FIN CONFIG ==========


  // ====== Normalizaci√≥n rol/usuario ======
  let ROLE = (proxy.currentRole || '').toString().trim();
  const USER = (proxy.currentUser || '').toString().trim().toUpperCase();
  if (CONFIG.roleAliases[ROLE]) ROLE = CONFIG.roleAliases[ROLE];   // num√©rico -> alias
  ROLE = ROLE.toUpperCase();

  // ====== Motor de reglas (first match wins) ======
  function resolveRule() {
    for (const r of CONFIG.rules) {
      const m = (r.match || {});
      if (m.default) return r;

      const okRole = !m.role || m.role.map(x => x.toString().toUpperCase()).includes(ROLE);
      const okUser = !m.user || m.user.map(x => x.toString().toUpperCase()).includes(USER);
      if (okRole && okUser) return r;
    }
    // si no hubo match, cae a "todo deshabilitado"
    return { disable: ['*'] };
  }

  function expand(list) {
    if (!list) return [];
    return list.includes('*') ? CONFIG.fields.slice() : list.slice();
  }

  const rule = resolveRule();
  const F_ENABLE   = expand(rule.enable);
  const F_READONLY = expand(rule.readonly);
  const F_DISABLE  = expand(rule.disable);

  // Prioridad: enable > readonly > disable
  function unique(a){ return Array.from(new Set(a)); }
  function without(a, b){ const B = new Set(b); return a.filter(x=>!B.has(x)); }

  const enableSet   = unique(F_ENABLE);
  let   readonlySet = unique(without(F_READONLY, enableSet));
  let   disableSet  = unique(without(F_DISABLE, enableSet.concat(readonlySet)));

  // ====== Utilidades DOM por patr√≥n de IDs ======
  function findControls(scope, field){
    const $s = $(scope || document);
    return $s.find(
      '[id^="value_'+field+'_"], [id^="display_value_'+field+'_"]'
    );
  }
  function findLookupTriggers(scope, field){
    return $(scope || document).find('[id^="open_lookup_'+field+'_"]');
  }

  function enableField(scope, field){
    const $els = findControls(scope, field).removeClass('ph-disabled');
    $els.each(function(){
      $(this).prop('disabled', false).prop('readonly', false);
      const $s2 = $(this).next('.select2-container, .select2');
      if ($s2.length) $s2.removeClass('ph-disabled').css('pointer-events','');
    });
    findLookupTriggers(scope, field)
      .removeClass('ph-disabled')
      .css('pointer-events','')
      .attr('aria-disabled', null)
      .off('click._lock');
  }

  function readonlyField(scope, field){
    const $els = findControls(scope, field).addClass('ph-disabled');
