// ================== CONFIG ==================
var fldProducto = 'idproducto';
var fldNoSerie  = 'noserie';
var fldDraft    = 'session_data_json';   // campo REAL en la tabla (Integrated edit field)
var ENDPOINT    = 'serial_reserve.php';

// Campos a restaurar/persistir (Step 1 + noserie)
var FIELDS_STEP1   = ['estado','ciudad','colonia','calle','numeroext','numeroint','noserie'];
var FIELDS_PERSIST = FIELDS_STEP1.concat(['idproducto']); // agrega m√°s si quieres

// ================== CONTROLES (inic. perezosa con getCtrl) ==================
function getCtrl(field){
  try { return Runner.getControl(pageid, field); } catch(e){ return null; }
}
var ctrlProducto = getCtrl(fldProducto);
var ctrlDraft    = getCtrl(fldDraft);

// ================== ESTADO ==================
window.__res_noserie = window.__res_noserie || null;
var lastAjax = { ts: 0, xhr: null };
var PREFILLING = false;

// ================== HELPERS ==================
function safeJson(resp){ if (typeof resp==='object') return resp; try{ return JSON.parse(resp);}catch(e){return null;} }
function isVisible(el){ return !!el && $(el).is(':visible') && $(el).css('visibility')!=='hidden' && $(el).width()>0 && $(el).height()>0; }

function setValSmart(field, value, opts){
  opts = opts || {};
  var c = getCtrl(field);
  if (c && typeof c.setValue==='function'){
    c.setValue(value);
    if (opts.fireChange && typeof c.fireEvent==='function') c.fireEvent('change');
    return true;
  }
  var $id = $('#value_'+field+'_'+pageid);
  if ($id.length){ $id.val(value).trigger('change'); return true; }
  var $w = $('div.r-integrated-field[data-field="'+field+'"][data-pageid="'+pageid+'"]');
  if ($w.length){
    var $inp = $w.find('input,select,textarea').first();
    if ($inp.length){ $inp.val(value).trigger('change'); return true; }
  }
  var $n = $('[name="'+field+'"]');
  if ($n.length){ $n.val(value).trigger('change'); return true; }
  return false;
}

function applyWhenReady(field, value, opts){
  var tries=0,max=40,delay=150;
  (function tick(){
    tries++;
    if (setValSmart(field,value,opts)) return;
    if (tries>=max) { console.warn('No se pudo setear', field); return; }
    setTimeout(tick,delay);
  })();
}

// === FIX: setter resiliente para NOSERIE ===
function setNoSerieValue(noserie){
  // 1) DOM inmediato (aunque el control no exista a√∫n)
  var $dom = $('#value_'+fldNoSerie+'_'+pageid);
  if ($dom.length){
    $dom.val(noserie).trigger('change');
    // Si es lookup/select2 en tu proyecto, descomenta:
    // $dom.trigger('change.select2');
  }
  // 2) Intento inmediato con Runner control
  var c = getCtrl(fldNoSerie);
  if (c && typeof c.setValue==='function'){
    c.setValue(noserie);
    if (typeof c.fireEvent==='function') c.fireEvent('change');
    return;
  }
  // 3) Reintentos hasta que el control exista (p.ej., cuando se muestre el Step)
  var tries=0,max=40,delay=150;
  (function retry(){
    tries++;
    var cc = getCtrl(fldNoSerie);
    if (cc && typeof cc.setValue==='function'){
      cc.setValue(noserie);
      if (typeof cc.fireEvent==='function') cc.fireEvent('change');
      return;
    }
    if (tries>=max) return;
    setTimeout(retry, delay);
  })();
}

// ================== AUTOSAVE ‚Üí session_data_json ==================
function readVal(field){
  var c = getCtrl(field);
  if (c && typeof c.getValue==='function') return c.getValue();
  var $id = $('#value_'+field+'_'+pageid); if ($id.length) return $id.val();
  var $w = $('div.r-integrated-field[data-field="'+field+'"][data-pageid="'+pageid+'"]');
  if ($w.length){ var $inp=$w.find('input,select,textarea').first(); if ($inp.length) return $inp.val(); }
  var $n = $('[name="'+field+'"]'); if ($n.length) return $n.val();
  return null;
}
function writeDraftJson(obj){
  var json = JSON.stringify(obj || {});
  var c = getCtrl(fldDraft);
  if (c && typeof c.setValue==='function'){ try{ c.setValue(json); }catch(e){} }
  var $id = $('#value_'+fldDraft+'_'+pageid);
  if ($id.length) $id.val(json);
}
function buildDraft(){ var d={}; FIELDS_PERSIST.forEach(function(f){ d[f]=readVal(f); }); return d; }
// Reemplaza tu makeDebounce por este:
var saveDraftDebounced = function(){ saveDraftNow(); };


var saveDraftNow       = function(){ writeDraftJson( buildDraft() ); };


// ================== RESERVA DE SERIE ==================
function releasePrev(){
  if (!window.__res_noserie) return;
  $.post(ENDPOINT, { action:'release', noserie: window.__res_noserie }, function(){}, 'json');
  window.__res_noserie = null;
}

function reserveForProduct(productId, opts){
  opts = opts || {};
  if (lastAjax.xhr && lastAjax.xhr.readyState!==4){ try{ lastAjax.xhr.abort(); }catch(e){} }
  var ts=Date.now(); lastAjax.ts = ts;

  var xhr = $.post(ENDPOINT, { action:'reserve_by_product', product_id: productId }, null, 'json')
    .done(function(resp){
      if (ts!==lastAjax.ts) return;
      resp = safeJson(resp) || {};
      if (resp.ok){
        setNoSerieValue(resp.noserie);      // <-- usa el setter resiliente
        window.__res_noserie = resp.noserie;
        saveDraftNow();
        if (!opts.silent) console.log('Reserva OK ‚Üí', resp.noserie);
      } else if (resp.error==='no-stock'){
        alert('No hay series disponibles para este producto.');
        var cProd = getCtrl(fldProducto); if (cProd) cProd.setValue('');
        setNoSerieValue('');
        releasePrev();
        saveDraftNow();
      } else {
        alert('No se pudo reservar una serie (concurrencia). Intenta de nuevo.');
        setNoSerieValue('');
        saveDraftNow();
      }
    })
    .fail(function(){
      if (ts!==lastAjax.ts) return;
      alert('Error de comunicaci√≥n al reservar la serie.');
      setNoSerieValue('');
      saveDraftNow();
    })
    .always(function(){ lastAjax.xhr=null; });

  lastAjax.xhr = xhr;
  return xhr;
}

// Cambio de producto
if (ctrlProducto){
  ctrlProducto.on('change', function(){
    if (PREFILLING) return;
    var productId = ctrlProducto.getValue();

    // limpia/invalida serie previa
    var prevSerie = readVal(fldNoSerie);
    if (prevSerie){
      setNoSerieValue(''); // limpia en DOM/Runner
      if (window.__res_noserie===prevSerie) releasePrev();
    } else {
      releasePrev();
    }

    // guarda draft (idproducto nuevo, noserie vac√≠a hasta reservar)
    saveDraftNow();

    if (!productId) return;
    reserveForProduct(productId);
  });
}

// Liberar y guardar al salir
window.addEventListener('beforeunload', function(){
  saveDraftNow();
  if (!window.__res_noserie) return;
  var data = new URLSearchParams({ action:'release', noserie: window.__res_noserie });
  try { navigator.sendBeacon(ENDPOINT, data); } catch(e){}
});

// ================== RESTAURAR DESDE session_data_json ==================
pageObj.on('afterInit', function(){

  // 1) leer borrador del campo oculto
  var draft = {};
  try{
    var raw = (getCtrl(fldDraft)&&getCtrl(fldDraft).getValue) ? getCtrl(fldDraft).getValue() : $('#value_'+fldDraft+'_'+pageid).val();
    if (raw) draft = JSON.parse(raw) || {};
  }catch(e){ draft = {}; }

  // 2) setear idproducto primero
  if (draft.idproducto != null && draft.idproducto !== ''){
    applyWhenReady('idproducto', draft.idproducto, {fireChange:true});
  }

  // 3) hidratar Step 1 cuando sea visible (direcci√≥n + noserie)
  var step1Sel = '#step_1_'+pageid;
  var step1El  = document.querySelector(step1Sel);

  var doHydrateStep1 = function(){
    FIELDS_STEP1.forEach(function(f){
      if (draft[f] != null && draft[f] !== '') applyWhenReady(f, draft[f], {});
    });

    if (draft.noserie){
      setNoSerieValue(draft.noserie);     // <-- tambi√©n usa el setter
      window.__res_noserie = draft.noserie;
    } else if (draft.idproducto){
      reserveForProduct(draft.idproducto, { silent:true });
    }

    // pulso de guardado por si el usuario no toca nada
    setTimeout(saveDraftNow, 100);
  };

  if (step1El && isVisible(step1El)){
    setTimeout(doHydrateStep1, 80);
  } else {
    $(document).on('click', '[data-stepcontrol] [data-step], .step-tab, .nav-link', function(){
      setTimeout(function(){
        var elNow = document.querySelector(step1Sel);
        if (elNow && isVisible(elNow)) doHydrateStep1();
      }, 200);
    });
    if (step1El){
      var obs = new MutationObserver(function(){
        if (isVisible(step1El)){ doHydrateStep1(); try{obs.disconnect();}catch(e){} }
      });
      obs.observe(step1El, { attributes:true, attributeFilter:['style','class'] });
    }
  }

  // 4) AUTOSAVE: cada cambio de los campos vigilados actualiza el JSON
  FIELDS_PERSIST.forEach(function(f){
    var c = getCtrl(f);
    if (c) c.on('change', saveDraftDebounced);
  });
  var selDeleg = FIELDS_PERSIST.map(function(f){
    return 'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pageid+'"] input, '+
           'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pageid+'"] select, '+
           'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pageid+'"] textarea';
  }).join(', ');
  $(document).on('change input', selDeleg, saveDraftDebounced);

  // 5) guardado peri√≥dico de seguridad
  var ticks=0,maxTicks=6;
  var safeTimer=setInterval(function(){ ticks++; saveDraftDebounced(); if(ticks>=maxTicks) clearInterval(safeTimer); },2000);
});

