// Helpers para armar valores seguros
function q($v){ // cadenas/fechas: 'valor' o NULL
    return (isset($v) && $v !== '' ? db_prepare_string($v) : "NULL");
}
function qi($v){ // enteros: número o NULL
    return (isset($v) && $v !== '' ? (int)$v : "NULL");
}
function qb($v){ // booleano/bit: 0/1
    return (!empty($v) ? 1 : 0);
}
function qd($v, $scale = 2) { // decimales: número (formateado) o NULL
    if (!isset($v) || $v === '') return "NULL";
    $n = (float)$v;
    return number_format($n, $scale, '.', ''); // sin comillas
}



/**
 * FUNCIÓN DE MANEJO DE FALLO (Solo hace el rollback y guarda datos)
 */
function handle_fail($values_to_save, $conn, $message_text) {
    DB::Exec("ROLLBACK", $conn);

    // Guardar todos los datos del formulario en la sesión para recuperarlos
    $_SESSION["add_data_temp"] = $values_to_save;
    $_SESSION["idactual"]=$_SESSION["add_data_temp"]['idproducto'];

    $_SESSION['currentErr']=$message_text;

    // Devolver el mensaje de error (string)
    return $message_text;
}

