(function () {
  /* ========== CONFIG (edita a tu gusto) ========== */
  const CONFIG = {
    // TODOS los campos que gobernarás (exactos)
    fields: ['estatus', 'estatusentrega'],

    // Si tus GroupID son números, mapéalos a nombres:
    roleAliases: { '1':'ADMIN', '2':'VALIDADOR' },

    // Reglas (la PRIMERA que coincida se aplica)
    // Usa enable / readonly / disable con arrays de campos o '*' para todos los de CONFIG.fields
    rules: [
      { match: { user: ['ADMIN'] }, enable: ['*'] },    
      { match: { role: ['ADMIN'] },       enable: ['*'] },                 // Admin: todo habilitado
      { match: { role: ['VALIDADOR'] },   enable: ['estatus'], disable: ['*'] }, // Validador: solo 'estatus'
      // Si “VENDEDORES” y “GERENTES” son ROLES (no usuarios), usa role: [...]
      { match: { role: ['VENDEDORES','GERENTES'] }, disable: ['estatus'] },      // Todo bloqueado
      { match: { default: true },         disable: ['*'] }                 // Fallback
    ]
  };
  /* ====== FIN CONFIG ====== */

  const DEBUG = false;
  const log = (...a)=>{ if (DEBUG) console.log('[lock]', ...a); };

  // ---- Normalización rol/usuario desde PHP ----
  let ROLE = (proxy.currentRole || '').toString().trim();
  const USER = (proxy.currentUser || '').toString().trim().toUpperCase();
  if (CONFIG.roleAliases[ROLE]) ROLE = CONFIG.roleAliases[ROLE];
  ROLE = ROLE.toUpperCase();

  // ---- Motor de reglas: first match wins ----
  function resolveRule() {
    for (const r of CONFIG.rules) {
      const m = r.match || {};
      if (m.default) return r;
      const byRole = !m.role || m.role.map(x=>x.toString().toUpperCase()).includes(ROLE);
      const byUser = !m.user || m.user.map(x=>x.toString().toUpperCase()).includes(USER);
      if (byRole && byUser) return r;
    }
    return { disable: ['*'] };
  }
  function expand(list) {
    if (!list) return [];
    return list.includes('*') ? CONFIG.fields.slice() : list.slice();
  }

  const rule = resolveRule();
  let enable   = new Set(expand(rule.enable));
  let readonly = new Set(expand(rule.readonly).filter(f=>!enable.has(f)));
  let disable  = new Set(expand(rule.disable).filter(f=>!enable.has(f) && !readonly.has(f)));

  const ALL = [...new Set([...enable, ...readonly, ...disable])];

  // ---- Selectores por patrón de IDs (PHPRunner) ----
  const qCtrl   = (f, scope)=>$(scope||document).find(`[id^="value_${f}_"],[id^="display_value_${f}_"]`);
  const qLookup = (f, scope)=>$(scope||document).find(`[id^="open_lookup_${f}_"]`);

  function doEnable(f, scope){
    const $els = qCtrl(f,scope).removeClass('ph-disabled');
    $els.each(function(){
      $(this).prop('disabled', false).prop('readonly', false);
      const $s2 = $(this).next('.select2-container, .select2');
      if ($s2.length) $s2.removeClass('ph-disabled').css('pointer-events','');
    });
    qLookup(f,scope).removeClass('ph-disabled').css('pointer-events','').attr('aria-disabled',null).off('click._lock');
  }
  function doReadonly(f, scope){
    const $els = qCtrl(f,scope).addClass('ph-disabled');
    $els.each(function(){
      if (this.tagName === 'SELECT') $(this).prop('disabled', true);
      else $(this).prop('readonly', true);
      const $s2 = $(this).next('.select2-container, .select2');
      if ($s2.length) $s2.addClass('ph-disabled').css('pointer-events','none');
    });
    // Si quieres bloquear también el "Seleccionar", descomenta:
    // qLookup(f,scope).addClass('ph-disabled').css('pointer-events','none')
    //   .attr('aria-disabled','true').on('click._lock', e => { e.preventDefault(); e.stopImmediatePropagation(); return false; });
  }
  function doDisable(f, scope){
    const $els = qCtrl(f,scope).addClass('ph-disabled');
    $els.each(function(){
      $(this).prop('disabled', true).prop('readonly', true);
      const $s2 = $(this).next('.select2-container, .select2');
      if ($s2.length) $s2.addClass('ph-disabled').css('pointer-events','none');
    });
    qLookup(f,scope).addClass('ph-disabled').css('pointer-events','none')
      .attr('aria-disabled','true').on('click._lock', e => { e.preventDefault(); e.stopImmediatePropagation(); return false; });
  }

  function applyScope(scope){
    enable.forEach(f=>doEnable(f,scope));
    readonly.forEach(f=>doReadonly(f,scope));
    disable.forEach(f=>doDisable(f,scope));
  }

  // ---- Espera activa a que existan los controles (rápida) ----
  function waitFor(list, timeout=2000, scope){
    const t0 = performance.now();
    return new Promise(resolve=>{
      (function tick(){
        const ok = list.every(f => $(scope||document).find(`[id^="value_${f}_"],[id^="display_value_${f}_"]`).length);
        if (ok || performance.now()-t0 > timeout) resolve();
        else requestAnimationFrame(tick);
      })();
    });
  }

  // ---- Aplicación inicial (por si ya hay inline abierto) ----
  applyScope(document);

  // ---- Guardias: si alguien alcanza a tocar un control, lo bloqueamos al instante ----
  const selInputs = ALL.map(f=>`[id^="value_${f}_"],[id^="display_value_${f}_"]`).join(',');
  if (selInputs) {
    $(document).on('focusin click', selInputs, function(){
      const id = this.id||'';
      const f = ALL.find(x => id.startsWith('value_'+x+'_') || id.startsWith('display_value_'+x+'_'));
      if (!f) return;
      if (disable.has(f))      doDisable(f, document);
      else if (readonly.has(f))doReadonly(f, document);
      else                     doEnable(f, document);
    });
  }
  const selLookup = ALL.map(f=>`[id^="open_lookup_${f}_"]`).join(',');
  if (selLookup) {
    $(document).on('click', selLookup, function(e){
      const id = this.id||'';
      const f = ALL.find(x => id.startsWith('open_lookup_'+x+'_'));
      if (!f) return;
      if (disable.has(f) || readonly.has(f)) {
        e.preventDefault(); e.stopImmediatePropagation();
        (disable.has(f) ? doDisable : doReadonly)(f, document);
        return false;
      }
    });
  }

  // ---- Al pulsar "Inline edit" ----
  $(document).on('click','[data-buttonevent="inlineEdit_start"], [data-itemid="grid_inline_edit"]', async function(){
    await waitFor(ALL, 2500, document);
    applyScope(document);
  });

  // ---- Observer: solo la grid (más rápido) ----
  const grid = document.querySelector('.r-grid') || document.body;
  new MutationObserver(async muts=>{
    for (const m of muts) {
      if (m.addedNodes && m.addedNodes.length) {
        await waitFor(ALL, 2000, grid);
        applyScope(grid);
        break;
      }
    }
  }).observe(grid, { childList:true, subtree:true });
})();





