// ====================== HELPERS ======================
function currentPageId() {
  if (typeof window.pageid !== 'undefined' && window.pageid) return window.pageid;
  var pid = $('[data-pageid]').first().attr('data-pageid');
  return pid || '';
}
function getCtrl(field) {
  try { return Runner.getControl(currentPageId(), field); } catch(e) { return null; }
}
function readVal(field) {
  var c = getCtrl(field);
  if (c && typeof c.getValue === 'function') return c.getValue();
  var pid = currentPageId();
  var $id = $('#value_'+field+'_'+pid);
  if ($id.length) return $id.val();
  var $w = $('div.r-integrated-field[data-field="'+field+'"][data-pageid="'+pid+'"]');
  if ($w.length) {
    var $inp = $w.find('input,select,textarea').first();
    if ($inp.length) return $inp.val();
  }
  var $n = $('[name="'+field+'"]');
  if ($n.length) return $n.val();
  return '';
}
function setVal(field, value, fireChange) {
  var c = getCtrl(field);
  if (c && typeof c.setControlValue === 'function') {
    c.setControlValue(value);
    if (fireChange && typeof c.fireEvent === 'function') c.fireEvent('change');
    return true;
  }
  var pid = currentPageId();
  var $id = $('#value_'+field+'_'+pid);
  if ($id.length) {
    $id.val(value).trigger('change');
    if (fireChange) $id.trigger('keyup');
    return true;
  }
  var $w = $('div.r-integrated-field[data-field="'+field+'"][data-pageid="'+pid+'"]');
  if ($w.length) {
    var $inp = $w.find('input,select,textarea').first();
    if ($inp.length) {
      $inp.val(value).trigger('change');
      if (fireChange) $inp.trigger('keyup');
      return true;
    }
  }
  return false;
}
function setNoSerieValue(noserie) {
  var ok = setVal('noserie', noserie, true);
  if (ok) return;
  var tries = 0, max = 40, delay = 150;
  (function retry(){
    tries++;
    if (setVal('noserie', noserie, true)) return;
    if (tries >= max) return;
    setTimeout(retry, delay);
  })();
}
function getQS(name){
  var m = new RegExp('[?&]'+name+'=([^&]*)').exec(location.search);
  return m ? decodeURIComponent(m[1].replace(/\+/g,' ')) : null;
}
function hasErrorMessage() {
  var $msg = $('[data-itemtype="edit_message"].alert, .alert[data-itemtype="edit_message"]');
  if (!$msg.length) return false;
  var txt = $.trim($msg.text() || '');
  if (!txt) return false;
  return $msg.is('.alert-danger, .alert-error') || true;
}

// ====================== AUTOSAVE (por registro) ======================
function buildEditDraft() {
  var d = {};
  (window.EDIT_FIELDS || []).forEach(function(f){
    var v = readVal(f);
    if (v !== null && v !== undefined && v !== '') d[f] = v;
  });
  d.timestamp = Date.now();
  return d;
}
function saveEditDraftNow(){
  try{ sessionStorage.setItem(window.EDIT_STORAGE_KEY, JSON.stringify(buildEditDraft())); }catch(e){}
}
function debounce(fn, ms){ var t=0; return function(){ var a=arguments,s=this; if(t){try{clearTimeout(t);}catch(e){}} t=setTimeout(function(){ t=0; try{fn.apply(s,a);}catch(e){} }, ms||200); }; }
var saveDraftDebounced = debounce(saveEditDraftNow, 300);

// ====================== Reserva por producto (EDIT) ======================
function releasePrev(){
  if (!window.__res_noserie) return;
  $.post(window.ENDPOINT, { action:'release', noserie: window.__res_noserie }, function(){}, 'json');
  window.__res_noserie = null;
}
function reserveForProduct(productId){
  $.post(window.ENDPOINT, { action:'reserve_by_product', product_id: productId }, null, 'json')
    .done(function(resp){
      var r = (typeof resp === 'object') ? resp : (function(){ try{return JSON.parse(resp);}catch(e){return {};}})();
      if (r && r.ok) {
        setNoSerieValue(r.noserie);
        window.__res_noserie = r.noserie;
        saveEditDraftNow();
      } else if (r && r.error === 'no-stock') {
        alert('No hay series disponibles para este producto.');
        setVal('idproducto','',true);
        setNoSerieValue('');
        releasePrev();
        saveEditDraftNow();
      } else {
        alert('No se pudo reservar la serie. Intenta de nuevo.');
        setNoSerieValue('');
        saveEditDraftNow();
      }
    })
    .fail(function(){
      alert('Error de comunicación al reservar la serie.');
      setNoSerieValue('');
      saveEditDraftNow();
    });
}

