  $(document).on('click', SAVE_SELECTOR, function(){
    window.__edit_submitting = true;
    saveEditDraftNow();
  });

  // 1) Modo limpieza tras éxito o entrada limpia
  var cleanMode = !!(window.EDIT_CLEAN_ON_LOAD) || (getQS('clean') === '1') || !!window.EDIT_SKIP_RECOVERY;
  if (cleanMode) {
    try{ sessionStorage.removeItem(window.EDIT_STORAGE_KEY); }catch(e){}
    // En EDIT **no** se “blanquea” el formulario: se deja el estado del servidor.
    // Sólo desactivamos la recuperación.
  } else {
    // 2) Recuperación desde sessionStorage si hubo error
    var draftJson = sessionStorage.getItem(window.EDIT_STORAGE_KEY);
    if (draftJson && !hasErrorMessage()) {
      try {
        var d = JSON.parse(draftJson)||{};
        var now = Date.now();
        // 2 horas por consistencia
        if (d.timestamp && (now - d.timestamp > 2*60*60*1000)) {
          sessionStorage.removeItem(window.EDIT_STORAGE_KEY);
        } else {
          window.__edit_recovering = true;
          // Producto primero
          if (d.idproducto != null && d.idproducto !== '') {
            setVal('idproducto', d.idproducto, true);
          }
          // Resto
          Object.keys(d).forEach(function(k){
            if (k==='timestamp' || k==='idproducto') return;
            setVal(k, d[k], false);
          });
          // Si había una serie en el draft, tómala como “visual”
          if (d.noserie) window.__res_noserie = d.noserie;

          sessionStorage.removeItem(window.EDIT_STORAGE_KEY);
          setTimeout(function(){ window.__edit_recovering=false; }, 150);
        }
      } catch(e) {
        sessionStorage.removeItem(window.EDIT_STORAGE_KEY);
