  // 4) Autosave periódico y por cambio
  setInterval(function(){ if(!window.__edit_recovering) saveEditDraftNow(); }, 5000);

  var selDeleg = (window.EDIT_FIELDS || []).map(function(f){
    return 'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pid+'"] input, '+
           'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pid+'"] select, '+
           'div.r-integrated-field[data-field="'+f+'"][data-pageid="'+pid+'"] textarea';
  }).join(', ');
  $(document).on('change input', selDeleg, saveDraftDebounced);

  // 5) Salidas (volver/cerrar) → liberar reserva y limpiar draft
  (function(){
    var BACK_SELECTOR = [
      '#backButton1',
      '#backButton2',
      "[data-itemtype='edit_back_list'] a",
      "[data-itemid='edit_back_list'] a",
      "[data-itemtype='edit_close'] a",
      "[data-itemid='edit_close'] a",
      "a[href$='solicitudes_list.php']",
      "a[href*='solicitudes_list.php?']"
    ].join(', ');
    function safeReleaseAndClear(){
      try{
        if (window.__res_noserie) {
          navigator.sendBeacon(window.ENDPOINT, new URLSearchParams({action:'release', noserie: window.__res_noserie}));
          window.__res_noserie = null;
        }
        sessionStorage.removeItem(window.EDIT_STORAGE_KEY);
      }catch(e){}
    }
    $(document).on('mousedown click', BACK_SELECTOR, function(){
      window.__edit_submitting = false;
      safeReleaseAndClear();
    });

    window.addEventListener('pagehide', function(){
      if (!window.__edit_submitting) {
        try{
          if (window.__res_noserie) {
            navigator.sendBeacon(window.ENDPOINT, new URLSearchParams({action:'release', noserie: window.__res_noserie}));
            window.__res_noserie = null;
          }
          sessionStorage.removeItem(window.EDIT_STORAGE_KEY);
        }catch(e){}
      }
    });
    window.addEventListener('beforeunload', function(){
      if (!window.__edit_submitting && window.__res_noserie) {
        try{
          navigator.sendBeacon(window.ENDPOINT, new URLSearchParams({action:'release', noserie: window.__res_noserie}));
        }catch(e){}
      }
    });
  })();

