global $pageObject, $xt, $conn;









/**
 * Helper para mandar un mensaje al cliente (OnLoad) que se verá
 * tanto en popup como en página normal.
 * Tipos: success | error | warning | info
 */
function pushPopupMsg($pageObject, $text, $type = 'info') {
    if (!$text) return;
    $pageObject->setProxyValue("popup_msg", [
        "text" => $text,
        "type" => $type
    ]);
}

/* =========================================================
   1) Reinyectar (opcional) datos tras error de validación
   ========================================================= */
if (isset($_SESSION['edit_data_temp']) && is_array($_SESSION['edit_data_temp'])) {
    $recovery = $_SESSION['edit_data_temp'];

    // Si quieres reinyectar directamente en controles que pertenecen
    // a la tabla principal, puedes habilitar este bloque:
    foreach ($recovery as $field => $value) {
        if ($value !== null && $value !== '') {
            // value_{campo}_{pageid}. En Edit suele ser "_1"
            $pageObject->setProxyValue("value_{$field}_1", $value);
        }
    }

    // Aviso al usuario
    pushPopupMsg($pageObject, "⚠️ Se recuperaron los datos del intento anterior. Revise y guarde nuevamente.", "warning");
    // NO limpiamos aquí; se limpia al guardar exitosamente en AfterRecordUpdated.
}

